package ContactAPI

import play.api.libs.json.Json
import scala.concurrent.Future
import scala.io.Source
import play.api.libs.json.JsObject
import play.api.libs.json.JsArray
import scala.concurrent.duration._
import scalaj.http.{Http, HttpOptions}
import scala.concurrent.Await
import scala.concurrent.ExecutionContext


trait TriggerCompanyUpdate {

  implicit val ex: ExecutionContext
  private val source = Source.fromResource("companies.json").getLines.mkString
  private val json = Json.parse(source)


//  def triggerCompanies() =
//    sendPutRequest.compose(() => readFields)


  private val readFields: () => Seq[Company] = () => {
    json.validate[JsObject].map{ ob =>
      ob.fields
        .filter(_._1 == "result")
        .map(_._2.validate[JsArray].get)
        .flatMap(e => e.value)
        .map(e => Company(
          e.apply("id").as[String],
          e.apply("additional_name").asOpt[String],
          e.apply("version").as[Int]))
    }.get
  }


  private val sendPutRequest = (companies: Seq[Company]) => {
    companies.foreach { company =>
      val send = Json.toJson(company.toPutch()).toString()
      println(s"body to send: $send")
      val waiter = wait123()
      Await.result(waiter, 7 seconds)

      val response = Http(s"http://ln-contacts-api.ln-test.liga.cloud/companies/${company.id}")
        .put(send)
        .method("PATCH")
        .header("Content-Type", "application/json")
        .header("Charset", "UTF-8")
        .header("Client-Id", "1")
        .header("Authorization", authString)
        .option(HttpOptions.readTimeout(10000)).asString

      println(s"response: $response")
    }
  }


  private def wait123(): Future[Unit] = Future(Thread.sleep(5000))
  private val authString = "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.._XUTjzyFaPSS5f9siuHrFn8Hxlyv20L8G5Znx0iamHg"
}
